require_once('workflows.php');
$zhihuUrl = "https://www.zhihu.com";
$articleUrl = "https://zhuanlan.zhihu.com/p";
$searchUrl = "https://www.zhihu.com/search?q=";

$wf = new Workflows();

$query = "{query}";

$response = $wf->request("https://www.zhihu.com/autocomplete?max_matches=10&use_similar=0&token=" . urlencode($query));
$content = json_decode($response);
$entries = $content[0];
$count = count($entries);

$types = ['question', 'article', 'people', 'topic', 'search_link'];

for ($i = 1; $i < $count; $i++) {
    $item = $entries[$i];

    $row = [
        'uid' => uniqid(),
        'arg' => '',
        'title' => $item[1],
        'subtitle' => "[$item[0]] $item[1]",
        'icon' => "appicons/$item[0].png",
        'valid' => 'yes',
        'autocomplete' => null,
    ];

    switch ($item[0]) {
        case 'question':
            $row['arg'] = "$zhihuUrl/$item[0]/$item[3]";
            $row['subtitle'] = "$item[4] 个回答";
            break;
        case 'article':
            $row['arg'] = "$articleUrl/$item[3]";
            $row['subtitle'] = "$item[4] 个赞";
            break;
        case 'people':
            $row['arg'] = "$zhihuUrl/$item[0]/$item[2]";
            if (isset($item[7]) && !empty($item[7])) {
                $row['title'] .= " ✓";
                $row['subtitle'] = "[$item[7]] $item[5]";
            } else {
                $row['subtitle'] = "$item[5]";
            }
            break;
        case 'topic':
            $row['arg'] = "$zhihuUrl/$item[0]/$item[2]";
            $row['subtitle'] = "$item[6] 个精华回答";
            break;
        case 'search_link':
            $row['arg'] = "$searchUrl" . urlencode($query);
            $row['title'] = "查看全部搜索结果";
            $row['subtitle'] = "";
            break;
        default:
            $row['arg'] = "$zhihuUrl/$item[0]/$item[2]";
            break;
    }

    $rows[$item[0]][] = $row;
}

foreach ($types as $type) {
    if (isset($rows[$type]) && is_array($rows[$type])) {
        foreach ($rows[$type] as $row) {
            $wf->result($row['uid'], $row['arg'], $row['title'], $row['subtitle'], $row['icon'], $row['valid'], $row['autocomplete']);
        }
    }
}

echo $wf->toxml();

require_once('workflows.php');
$wf = new Workflows();
$response = $wf->request("http://news.at.zhihu.com/api/1.2/news/latest");
$content = json_decode($response);

$news = $content->news;
$count = count($news);

for( $i = 0; $i < $count; $i++) {
    $item = $news[$i];
    $wf->result(uniqid(), $item->share_url , $item->title, '', '');
}

echo $wf->toxml();
